mapper.select:
select score.*, student.name as studentName, course.name as courseName, teacher.name as teacherName
from score
left join student on score.student_id = student.id
left join course on score.course_id = course.id
left join teacher on score.teacher_id = teacher.id

     and id= #{id}
     and score.student_id= #{studentId}
     and score.course_id= #{courseId}
     and score.teacher_id= #{teacherId}

order by id desc

mapper.update:
update score
student_id = #{studentId}, course_id = #{courseId}, teacher_id = #{teacherId}, ordinary_score1 = #{ordinaryScore1}, ordinary_score2 = #{ordinaryScore2}, ordinary_score3 = #{ordinaryScore3}, ordinary_score4 = #{ordinaryScore4}, ordinary_score5 = #{ordinaryScore5}, ordinary_score6 = #{ordinaryScore6}, exam_score = #{examScore}, score = #{score},
where id = #{id}
